class ShoeView(TemplateView):
    def get(self, *args, **kwargs):

        shoe = shopify_get_product(self.kwargs['shoe_id'])
        attributes = {
            "design": {"title": "Design", "image": "data:image/svg+xml;base64,"},
            "raw_materials": {"title": "Raw Materials", "image": "data:image/svg+xml;base64,"},
            "material_manufacturing": {"title": "Material Manufacturing", "image": "data:image/svg+xml;base64,"},
            "footwear_manufacturing": {"title": "Footwear Manufacturing", "image": "data:image/svg+xml;base64,"},
            "retail": {"title": "Retail", "image": "data:image/svg+xml;base64,"},
            "use": {"title": "Use", "image": "data:image/svg+xml;base64,"},
            "disposal": {"title": "Disposal", "image": "data:image/svg+xml;base64,"},
            "governance": {"title": "Governance", "image": "data:image/svg+xml;base64,"}
        }
        return render(self.request, "shoe.html", {"shoe": shoe, "attributes": attributes})